package com.syntax.class03;

public class IfElse5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int money=66000;
		if(money>=65000) {// it means if the is greater or equal to 65000
			System.out.println("i can buy a tasla");
			
		}else {
			System.out.println("i need save more money");
		}
		
		if(money==65000) {// ==it means if the is exactly the same
			System.out.println("i can buy a tasla");
			
		}
	}

}
