package com.syntax.class09;

public class Pattrents1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i<=+4; i++) {// outer loop take care of rows
			for (int j = 1; j<=+3; j++) {// inner loop take care of coloms
				System.out.print("* ");
		}
			System.out.println();// outer loop print space
			
			// number of colom always depends on number of rows in tringle 
		}
	}

}
